<?php        defined('_ACCESS') or die;        class TermEditor extends Visual {            private static $TEMPLATE = "articles/terms/editor.tpl";            private $_template_engine;        private $_current_term;            public function __construct($current_term) {            $this->_current_term = $current_term;            $this->_template_engine = TemplateEngine::getInstance();        }            public function render() {            $this->_template_engine->assign("id", $this->_current_term->getId());                        $this->_template_engine->assign("name_field", $this->renderNameField());            return $this->_template_engine->fetch("modules/" . self::$TEMPLATE);        }                private function renderNameField() {            $name_value = null;            if (isset($this->_current_term)) {                 $name_value = $this->_current_term->getName();            }            $name_field = new TextField("name", "Naam", $name_value, true, false, null);            return $name_field->render();        }    }    ?>