<?php	// No direct access	defined('_ACCESS') or die;		require_once FRONTEND_REQUEST . "view/request_handlers/module_request_handler.php";	require_once FRONTEND_REQUEST . "database/dao/authorization_dao.php";		class AuthorizationPreHandler extends ModuleRequestHandler {		private $_authorization_dao;		private $_current_user;				public function __construct() {			$this->_authorization_dao = AuthorizationDao::getInstance();		}			public function handleGet() {			$this->getCurrentUserFromGetRequest();		}				public function handlePost() {		}				public function getCurrentUser() {			return $this->_current_user;		}				private function getCurrentUserFromGetRequest() {			if (isset($_GET["user"])) {				$user_id = $_GET["user"];				$this->_current_user = $this->_authorization_dao->getUserById($user_id);			} else {				$this->_current_user = $this->_authorization_dao->getUser($_SESSION["username"]);			}		}			}	?>