<?php	// No direct access	defined('_ACCESS') or die;		require_once FRONTEND_REQUEST . "view/request_handlers/module_request_handler.php";	require_once FRONTEND_REQUEST . "database/dao/block_dao.php";		class PositionPreHandler extends ModuleRequestHandler {		private static $POSITION_ID_POST = "position";		private static $POSITION_ID_GET = "position";		private $_block_dao;		private $_current_position;				public function __construct() {			$this->_block_dao = BlockDao::getInstance();		}				public function handleGet() {			$this->_current_position = $this->getPositionFromGetRequest();		}				public function handlePost() {			$this->_current_position = $this->getPositionFromPostRequest();		}				public function getCurrentPosition() {			return $this->_current_position;		}				private function getPositionFromGetRequest() {			if (isset($_GET[self::$POSITION_ID_GET]))				return $this->getPositionFromDatabase($_GET[self::$POSITION_ID_GET]);		}				private function getPositionFromPostRequest() {			if (isset($_POST[self::$POSITION_ID_POST]))				return $this->getPositionFromDatabase($_POST[self::$POSITION_ID_POST]);		}				private function getPositionFromDatabase($position_id) {				return $this->_block_dao->getBlockPosition($position_id);		}	}	?>