<?php	// No direct access	defined('_ACCESS') or die;		require_once "libraries/system/template_engine.php";	require_once "core/visual/form_checkbox_single.php";	require_once "core/visual/information_message.php";		class PositionList extends Visual {			private static $TEMPLATE = "blocks/position_list.tpl";			private $_template_engine;		private $_block_dao;			public function __construct() {			$this->_template_engine = TemplateEngine::getInstance();			$this->_block_dao = BlockDao::getInstance();		}			public function render() {			$this->_template_engine->assign("all_positions", $this->getAllPositions());			$no_positions_message = new InformationMessage("Geen posities gevonden");			$this->_template_engine->assign("no_positions_message", $no_positions_message->render());						return $this->_template_engine->fetch("modules/" . self::$TEMPLATE);		}				private function getAllPositions() {			$all_positions_values = array();			$all_positions = $this->_block_dao->getBlockPositions();						foreach ($all_positions as $position) {				$position_value = array();				$position_value["id"] = $position->getId();				$position_value["name"] = $position->getName();				$position_value["explanation"] = $position->getExplanation();				$delete_field = new SingleCheckbox("position_" . $position->getId() . "_delete", "", false, false, "");				$position_value["delete_field"] = $delete_field->render();								$all_positions_values[] = $position_value;			}			return $all_positions_values;		}	}	?>