<?php	// No direct access	defined('_ACCESS') or die;		require_once "libraries/system/template_engine.php";	require_once "core/visual/information_message.php";		class ImageList extends Visual {			private static $TEMPLATE = "images/images/image_list.tpl";			private $_template_engine;		private $_current_image;		private $_image_dao;			public function __construct($current_image) {			$this->_current_image = $current_image;			$this->_image_dao = ImageDao::getInstance();			$this->_template_engine = TemplateEngine::getInstance();		}			public function render() {			$this->_template_engine->assign("search_results", $this->getSearchResults());			$this->_template_engine->assign("no_results_message", $this->renderNoResultsMessage());						return $this->_template_engine->fetch("modules/" . self::$TEMPLATE);		}				private function renderNoResultsMessage() {			$no_result_message = new InformationMessage("Geen afbeeldingen gevonden");			return $no_result_message->render();		}				private function getSearchResults() {			$images = array();						// search images						return $images;		}				private function toArray($image) {			$image_value = array();			$image_value["id"] = $image->getId();			$image_value["title"] = $image->getTitle();			$image_value["published"] = $image->isPublished();						return $image_value;		}		}	?>